import numpy as np

def hungarian_assignment(cost_matrix):
    n = len(cost_matrix)
    m = len(cost_matrix[0])

    # Create a matrix to store the minimum cost
    min_cost = [[0] * m for _ in range(n)]

    # Create a matrix to store the minimum cost path
    min_path = [[0] * m for _ in range(n)]

    # Initialize the minimum cost matrix
    for i in range(n):
        for j in range(m):
            min_cost[i][j] = cost_matrix[i][j]

    # Find the minimum cost for each row
    for i in range(n):
        min_val = np.inf
        min_index = -1
        for j in range(m):
            if min_cost[i][j] < min_val:
                min_val = min_cost[i][j]
                min_index = j
        min_path[i][min_index] = 1
        for k in range(m):
            if k != min_index:
                min_cost[i][k] = np.inf

    # Find the minimum cost for each column
    for j in range(m):
        min_val = np.inf
        min_index = -1
        for i in range(n):
            if min_cost[i][j] < min_val:
                min_val = min_cost[i][j]
                min_index = i
        min_path[min_index][j] = 1
        for k in range(n):
            if k != min_index:
                min_cost[k][j] = np.inf

    # Find the minimum cost path
    for i in range(n):
        for j in range(m):
            if min_path[i][j] == 1:
                min_cost[i][j] = 0

    return min_cost
