def greedy_graph_coloring(graph):
    color_assignment = {node: None for node in graph}
    color_choices = list(range(len(graph)))

    for node in sorted(graph):
        color_choices_for_node = [
            color for color in color_choices
            if not any(
                graph[neighbor] and color_assignment[neighbor] == color
                for neighbor in graph[node]
            )
        ]
        color_assignment[node] = color_choices_for_node.pop()

    return color_assignment
