import collections

def articulation_points(graph):
    time = 0
    low_time = collections.defaultdict(int)
    articulation_points = set()
    root = None

    def visit(node, parent):
        nonlocal time
        time += 1
        low_time[node] = time

        children = 0
        for neighbor in graph[node]:
            if neighbor == parent:
                continue

            if not low_time[neighbor]:
                children += 1
                visit(neighbor, node)

                low_time[node] = min(low_time[node], low_time[neighbor])

                if low_time[neighbor] >= time:
                    articulation_points.add(node)

            elif neighbor in graph[node]:
                low_time[node] = min(low_time[node], low_time[neighbor])

        if root is None and children > 1:
            root = node

    for node in graph:
        if not low_time[node]:
            visit(node, None)

    return articulation_points if root else None
