from collections import deque

def cycle_detection(graph):
    visited = set()
    rec_stack = set()

    for node in graph:
        if node not in visited:
            if is_cyclic_util(node, graph, visited, rec_stack):
                return True

    return False

def is_cyclic_util(node, graph, visited, rec_stack):
    visited.add(node)
    rec_stack.add(node)

    for neighbor in graph[node]:
        if neighbor not in visited:
            if is_cyclic_util(neighbor, graph, visited, rec_stack):
                return True
        elif neighbor in rec_stack:
            return True

    rec_stack.remove(node)
    return False

# Example usage:
graph = {
    0: [1, 2],
    1: [2],
    2: [3],
    3: [3]
}
print(cycle_detection(graph))  # Output: True
