def is\_connected(graph):
for node in graph:
visited = set()

def dfs(current\_node):
if current\_node not in visited:
visited.add(current\_node)
for neighbor in graph[current\_node]:
dfs(neighbor)

dfs(node)
if len(visited) < len(graph):
return False
return True

# Example usage:
graph = {
'A': ['B', 'C'],
'B': ['A', 'D', 'E'],
'C': ['A', 'F'],
'D': ['B'],
'E': ['B', 'F'],
'F': ['C', 'E']
}

print(is\_connected(graph))
