from itertools import combinations

def get_cliques(nodes, edges):
    cliques = []
    for i in range(2, len(nodes) + 1):
        for comb in combinations(nodes, i):
            if is_clique(comb, edges):
                cliques.append(list(comb))
    return cliques

def is_clique(nodes, edges):
    for node in nodes:
        for edge in combinations(nodes, 2):
            if edge not in edges and node not in edge:
                return False
    return True

nodes = [1, 2, 3, 4]
edges = {(1, 2), (2, 3), (3, 4), (1, 4)}
print(get_cliques(nodes, edges))
