def git_switch_branch(
    branch: str, repo: Path, new: bool = False, from_branch: str | None = None
) -> None:
    args = ["git", "checkout"]
    if new:
        args.append("-b")
    args.append(branch)
    if from_branch:
        args.append(from_branch)
    subprocess.run(args, cwd=repo)
