def readHotkey(
        self, runningTitle="<<readHotkeyRunning>>", finishTitle="<<readHotkeyFinish>>"
    ):
        self._start()
        if self._status == 1:
            return (
                "[Warning] Recording is running. 当前快捷键录制已在进行，不能同时录制！"
            )
        self._status = 1
        self._readRunningTitle = runningTitle
        self._readFinishTitle = finishTitle
        return "[Success]"
