def copyImage(path):
    im = _imread(path)
    typ, data = im["type"], im["data"]
    if typ == "error":
        return data
    try:
        if typ == "pixmap":
            Clipboard.setPixmap(data)
        elif typ == "qimage":
            Clipboard.setImage(data)
        return "[Success]"
    except Exception as e:
        return f"[Error] can't copy: {e}\n{path}"
