def saveImage(fromPath, toPath):
    if toPath.startswith("file:///"):
        toPath = toPath[8:]
    im = _imread(fromPath)
    typ, data = im["type"], im["data"]
    if typ == "error":
        return data
    try:
        if typ == "pixmap":
            data.save(toPath)
        elif typ == "qimage":
            data.save(toPath)
        return f"[Success] {toPath}"
    except Exception as e:
        return f"[Error] can't save: {e}\n{fromPath}\n{toPath}"
