def setPilImage(self, img, imgID=""):
        try:
            pixmap = ImageQt.toqpixmap(img)
        except Exception as e:
            logger.error("PIL 转 QPixmap 失败。", exc_info=True, stack_info=True)
            return f"[Error] PIL 转 QPixmap 失败：{e}"
        if not imgID:
            imgID = str(uuid4())
        self.pixmapDict[imgID] = pixmap
        return imgID
