def download_file(url, path):
    logger.info(f"Downloading {url} to {path}")
    block_size = 1024 * 1024
    with urllib.request.urlopen(url) as response, open(path, "wb") as file:
        while True:
            data = response.read(block_size)
            if not data:
                break
            file.write(data)
