def longlines():
    with open(file_name, encoding="utf-8") as F:
        lines = F.readlines()
        lines_less_than_50 = list(filter(lambda line: len(line) < 50, lines))

        if not lines_less_than_50:
            print("There is no line which is less than 50")
        else:
            for i in lines_less_than_50:
                print(i, end="\t")
