def get_property(self, key: str) -> Any:
        keys = key.split(".")

        config = self.file.read() if self.file.exists() else {}

        for i, key in enumerate(keys):
            if key not in config:
                raise PropertyNotFoundError(f"Key {'.'.join(keys)} not in config")

            if i == len(keys) - 1:
                return config[key]

            config = config[key]
